import React, { useState } from "react";

function App() {

  const array=["Ahmed","Jibran","Zain"];


  const [msg,setMsg]=useState("");

  const [name,setName]=useState("Hello")
  function showText(event){
    array.forEach(function(element){
      if(event.target.value === element){
        setMsg("User Name Already Taken");
      }
      else{
        setMsg("Valid Username");
      }
      setName(event.target.value);
    });
  }

  return (
    <div className="container">
      <h1>{name}</h1>
      <input onChange={showText} type="text" placeholder="What's your name?" />
      <button>Submit</button><span>{msg}</span>
    </div>
  );
}

export default App;
